mapper.select:
select
from bh_device_config where status = 0 AND del_status = 0 AND user_id = #{userId,jdbcType=INTEGER}
AND device_name LIKE CONCAT('%', #{deviceName}, '%')

select sa.id, sa.user_id, sa.stream_media_id, sa.device_number, sa.device_channel, sa.device_name, sa.device_account, sa.device_password, sa.manufacturer, sa.resolution_ratio, sa.visit_address, sa.distinguish_type, sa.server_ip, sa.sip_server_ip, sa.status, sa.del_status, sa.create_time from bh_device_config as sa
sa.del_status=0 AND sa.user_id = #{userId}
AND ( sa.device_number LIKE CONCAT('%', #{keyword}, '%') or sa.device_name LIKE CONCAT('%', #{keyword}, '%'))
order by sa.create_time DESC
LIMIT #{pageNum},#{pageSize}

select count(1) from bh_device_config as sa
sa.del_status=0 AND sa.user_id = #{userId}
AND ( sa.device_number LIKE CONCAT('%', #{keyword}, '%') or sa.device_name LIKE CONCAT('%', #{keyword}, '%'))
