    /**
     * 处理图片，画出识别框
     * @param path
     * @param local
     * @throws IOException
     */
    public static void drawFrame(String path, String local) throws IOException {
        JSONArray array = JSONArray.fromObject(local);
        if(array.size() != 0) {
            BufferedImage image = ImageIO.read(new File(path));

            Graphics g = image.getGraphics();
            g.setColor(Color.green);//画笔颜色
            for (int i = 0; i < array.size(); i++) {
                JSONArray obj = array.getJSONArray(i);
                //矩形框(原点x坐标，原点y坐标，矩形的长，矩形的宽)
                g.drawRect(obj.getInt(0), obj.getInt(1), obj.getInt(2) - obj.getInt(0), obj.getInt(3) - obj.getInt(1));
                //g.dispose();
                System.out.println("识别度：" + obj.getInt(4) / 1000 + "%");
            }
            String outPath = "C:\\image\\deal_image\\";
            File file = new File(outPath);
            if (!file.exists()) {//如果文件夹不存在
                file.mkdirs();//创建文件夹
            }
            String outName = "image.jpg";
            FileOutputStream out = new FileOutputStream(outPath + outName);//输出图片的地址
            ImageIO.write(image, "jpeg", out);
        }
    }

    public static void main2(String[] args) {
        final BlockingQueue queue = new ArrayBlockingQueue(100);
        for(int i=0;i<2;i++){
            new Thread(){
                public void run(){
                    while(true){
                        try {
                            Thread.sleep((long)(Math.random()*1000));
                            System.out.println(Thread.currentThread().getName() + "准备放数据!");
                            queue.put(1);
                            System.out.println(Thread.currentThread().getName() + "已经放了数据，" +
                                    "队列目前有" + queue.size() + "个数据");
                        } catch (InterruptedException e) {
                            e.printStackTrace();
                        }
                    }
                }
            }.start();
        }

        new Thread(){
            public void run(){
                while(true){
                    try {
                        //将此处的睡眠时间分别改为100和1000，观察运行结果
                        Thread.sleep(1000);
                        System.out.println(Thread.currentThread().getName() + "准备取数据!");
                        Integer i = (Integer) queue.remove();
                        System.out.println(Thread.currentThread().getName() + "已经取走数据，" +
                                "队列目前有" + queue.size() + "个数据:"+i);
                    } catch (InterruptedException e) {
                        e.printStackTrace();
                    }
                }
            }
        }.start();
    }
